package irvine.oeis.a036;

import irvine.math.polynomial.CycleIndex;
import irvine.math.polynomial.MultivariateMonomial;
import irvine.math.q.Q;
import irvine.math.z.Z;
import irvine.oeis.a000.A000598;

/**
 * A036723 G.f. satisfies A(x) = 1 + x*cycle_index(G,A(x)) where G = cyclic group of order 2 generated by (1,2)(3,4).
 * @author Sean A. Irvine
 */
public class A036723 extends A000598 {

  private static final CycleIndex CYCLE_INDEX = new CycleIndex("G",
    MultivariateMonomial.create(1, Z.FOUR, Q.HALF),
    MultivariateMonomial.create(2, Z.TWO, Q.HALF));

  @Override
  protected CycleIndex cycleIndex() {
    return CYCLE_INDEX;
  }
}

