package irvine.oeis.a036;

import irvine.math.group.CyclicGroup;
import irvine.math.polynomial.CycleIndex;
import irvine.oeis.a000.A000598;

/**
 * A036735 G.f. satisfies A(x) = 1 + x*cycle_index(G,A(x)) where G = cyclic group of order 31 generated by (1,2,...,31).
 * @author Sean A. Irvine
 */
public class A036735 extends A000598 {

  private static final CycleIndex CYCLE_INDEX = new CyclicGroup(31).cycleIndex();

  @Override
  protected CycleIndex cycleIndex() {
    return CYCLE_INDEX;
  }
}

