package irvine.oeis.a048;

import java.util.TreeSet;

import irvine.factor.factor.PrimeDivision;
import irvine.math.z.Binomial;
import irvine.math.z.Z;
import irvine.oeis.Sequence1;

/**
 * A048687 a(n) is the number of classes generated by function A001221 when applied to binomial coefficients.
 * @author Sean A. Irvine
 */
public class A048687 extends Sequence1 {

  private final PrimeDivision mFactor = new PrimeDivision();
  private long mN = 0;

  @Override
  public Z next() {
    ++mN;
    final TreeSet<Integer> seen = new TreeSet<>();
    for (long k = 0; k <= mN / 2; ++k) {
      seen.add(mFactor.factorize(Binomial.binomial(mN, k)).omega());
    }
    return Z.valueOf(seen.size());
  }
}

