package irvine.oeis.a052;

import irvine.math.group.PolynomialRingField;
import irvine.math.polynomial.Polynomial;
import irvine.math.q.Q;
import irvine.math.q.Rationals;
import irvine.math.z.Z;
import irvine.oeis.Sequence0;

/**
 * A052818 Number of objects generated by the Combstruct grammar defined in the Maple program. See the link for the grammar specification.
 * @author Sean A. Irvine
 */
public class A052818 extends Sequence0 {

  private static final PolynomialRingField<Q> RING = new PolynomialRingField<>(Rationals.SINGLETON);
  private Polynomial<Q> mA = RING.x();
  private int mN = -1;

  @Override
  public Z next() {
    if (++mN > 0) {
      mA = RING.series(RING.x(), RING.subtract(RING.one(), A052815.cik(mA, mN)), mN);
    }
    return mA.coeff(mN).toZ();
  }
}
