package irvine.oeis.a052;

import irvine.math.group.PolynomialRingField;
import irvine.math.polynomial.Polynomial;
import irvine.math.q.Q;
import irvine.math.q.Rationals;
import irvine.math.z.Z;
import irvine.oeis.Sequence0;

/**
 * A052893 Number of objects generated by the Combstruct grammar defined in the Maple program. See the link for the grammar specification.
 * @author Sean A. Irvine
 */
public class A052893 extends Sequence0 {

  private static final PolynomialRingField<Q> RING = new PolynomialRingField<>(Rationals.SINGLETON);
  private int mN = -1;
  private Polynomial<Q> mS = RING.one();

  @Override
  public Z next() {
    if (++mN > 0) {
      final Polynomial<Q> b = mS.shift(1);
      Polynomial<Q> c = RING.zero();
      for (int k = 1; k <= mN; ++k) {
        c = RING.add(c, RING.divide(b.substitutePower(k, mN), new Q(k)));
      }
      c = RING.subtract(RING.exp(c, mN), RING.one());
      mS = RING.series(RING.one(), RING.subtract(RING.one(), c), mN);
    }
    return mS.coeff(mN).toZ();
  }
}

