package irvine.oeis.a069;

import irvine.math.function.Function1;
import irvine.math.function.Functions;
import irvine.math.z.Frobenius;
import irvine.math.z.Z;
import irvine.oeis.Sequence2;

/**
 * A069757 Frobenius number of the numerical semigroup generated by three consecutive pentagonal numbers.
 * @author Sean A. Irvine
 */
public class A069757 extends Sequence2 {

  private long mN = 1;
  private final Function1 mFunction;

  protected A069757(final Function1 function) {
    mFunction = function;
  }

  /** Construct the sequence. */
  public A069757() {
    this(Functions.PENTAGONAL);
  }

  @Override
  public Z next() {
    ++mN;
    final Z p1 = mFunction.z(mN);
    final Z p2 = mFunction.z(mN + 1);
    final Z p3 = mFunction.z(mN + 2);
    return Frobenius.frobenius(p1, p2, p3);
  }
}
