package irvine.oeis.a075;

import irvine.math.group.PolynomialRingField;
import irvine.math.polynomial.Polynomial;
import irvine.math.q.Q;
import irvine.math.q.Rationals;
import irvine.math.z.Z;
import irvine.oeis.Sequence0;

/**
 * A075820 Coefficients of power series generated by the continued fraction-like form: A(x) = 1/(1 - x/sqrt(1 - 4x/cube-root(1 - 9x/4th-root(1 - 16x/5th-root(1 - 25x/... /n-th-root(1 - (n^2)x/...)))))), where n-th-root(z)=z^(1/n).
 * @author Sean A. Irvine
 */
public class A075820 extends Sequence0 {

  private static final PolynomialRingField<Q> RING = new PolynomialRingField<>(Rationals.SINGLETON);
  private int mN = -1;

  @Override
  public Z next() {
    Polynomial<Q> a = Polynomial.create(Q.ONE, Q.valueOf(++mN));
    for (int k = 0; k < mN; ++k) {
      final Polynomial<Q> b = RING.subtract(RING.one(), RING.multiply(a.shift(1), new Q(mN - k).square()));
      a = RING.exp(RING.multiply(RING.log(b, mN), new Q(-1, mN - k)), mN);
    }
    return a.coeff(mN).toZ();
  }
}
