package irvine.oeis.a078;

import irvine.math.z.Z;
import irvine.oeis.Sequence1;

/**
 * A078625 Primenomial primes: primes generated by polynomials of degree n with sequentially increasing prime coefficients. This is for n = 3 or 2x^3 + 3x^2 + 5x + 7.
 * @author Sean A. Irvine
 */
public class A078625 extends Sequence1 {

  private long mN = -1;

  @Override
  public Z next() {
    while (true) {
      final Z t = Z.TWO.multiply(++mN).add(3).multiply(mN).add(5).multiply(mN).add(7);
      if (t.isProbablePrime()) {
        return t;
      }
    }
  }
}

