package irvine.oeis.a083;

import irvine.math.q.Q;
import irvine.oeis.gf.GfSequence;

/**
 * A083691 Length of list generated by n replacements of k by {-1-|k|, ..., 1+|k|} with increment 2, starting with {0}.
 * @author Sean A. Irvine
 */
public class A083691 extends GfSequence {

  /** Construct the sequence. */
  public A083691() {
    super(0, SQ.shift(SQ.reversion(SQ.divide(
      SQ.subtract(SQ.shift(SQ.sqrt(SQ.create(Q.ONE, Q.EIGHT, Q.EIGHT)), 2), SQ.create(Q.ZERO, Q.FOUR, Q.FIVE)),
      SQ.create(new Q(-4), new Q(-12), new Q(-12), new Q(-4)))), -1)
    );
  }
}
