import irvine.math.z.Z;
import irvine.oeis.FilterNumberSequence;

/**
 * A110016 Numbers n such that (n^2-7)/6 is prime.
 * @author Georg Fischer
 */
public class A110016 extends FilterNumberSequence {

  /** Construct the sequence. */
  public A110016() {
    super(1, 0, n -> {
      final Z v = Z.valueOf(n).square().subtract(7);
      return v.mod(6) == 0 && v.divide(6).isProbablePrime();
    });
  }
}
