package irvine.oeis.a121;

import irvine.math.z.Z;
import irvine.oeis.recur.ConstantOrderRecurrence;

/**
 * A121390 Sequence generated by the recurrence below begins with exactly seven primes.
 * <code>a(0) = 2; a(1) = 8607769; a(n) = a(n-2)*a(n-1) + a(n-2) + a(n-1)</code>
 * @author Georg Fischer
 */
public class A121390 extends ConstantOrderRecurrence {

  /** Construct the sequence. */
  public A121390() {
    super(0, 2, 0, 2, 8607769);
  }

  @Override
  protected Z compute(final int n) {
    return a(n - 1).multiply(a(n - 2)).add(a(n - 2)).add(a(n - 1));
  }
}
