package irvine.oeis.a140;

import java.util.ArrayList;
import java.util.List;

import irvine.math.z.Z;
import irvine.oeis.FiniteSequence;

/**
 * A140718 Let S be the 28 primes generated by the polynomial 2*x^2+29, for 1&lt;=x&lt;=28. This finite sequence is S interleaved with the reverse of S and with each term repeated 4 times.
 * @author Sean A. Irvine
 */
public class A140718 extends FiniteSequence {

  private static List<Z> build() {
    final ArrayList<Z> res = new ArrayList<>();
    for (long k = 1; k < 29; ++k) {
      final Z t = Z.valueOf(2 * k * k + 29);
      res.add(t);
      res.add(t);
      res.add(t);
      res.add(t);
      final Z u = Z.valueOf(2 * (29 - k) * (29 - k) + 29);
      res.add(u);
      res.add(u);
      res.add(u);
      res.add(u);
    }
    return res;
  }

  /** Construct the sequence. */
  public A140718() {
    super(1, FINITE, build());
  }
}
