package irvine.oeis.a157;

import irvine.math.z.Z;
import irvine.oeis.a006.A006345;

/**
 * A157238 <code>0-1</code> sequence generated by starting with <code>a 0,</code> and then by using whichever of 0, 1 will result in the shortest sequence repeated at the end.
 * @author Sean A. Irvine
 */
public class A157238 extends A006345 {

  @Override
  public Z next() {
    return super.next().subtract(1);
  }
}

