package irvine.oeis.a191;
// manually a190803 at 2021-11-16

import irvine.math.z.Z;
import irvine.oeis.PrependSequence;
import irvine.oeis.a190.A190803;

/**
 * A191285 Increasing sequence S generated by these rules: 1 is in S, and if x is in S then 3x and floor((x^2)/2) are in S.
 * @author Georg Fischer
 */
public class A191285 extends PrependSequence {

  private static class MySequence extends A190803 {
    @Override
    public void insertTerms(final Z x) {
      insert(x.multiply(x).divide2());
      insert(x.multiply(3));
    }
  }

  /** Construct the sequence. */
  public A191285() {
    super(new MySequence().skip(0), 0);
  }
}
