package irvine.oeis.a191;
// manually a190803 at 2021-09-02 11:05

import irvine.math.z.Z;
import irvine.oeis.a190.A190803;

/**
 * A191288 Increasing sequence generated by these rules: 1 is in a, and if x is in a then 2x and floor((x^2)/3) are in a.
 * @author Georg Fischer
 */
public class A191288 extends A190803 {

  /** Construct the sequence. */
  public A191288() {
    mN = -1;
  }

  @Override
  public void insertTerms(final Z x) {
    insert(x.multiply2());
    insert(x.multiply(x).divide(3));
  }

  @Override
  public Z next() {
    ++mN;
    if (mN == 0) {
      return Z.ZERO;
    }
    mX = mA.pollFirst();
    insertTerms(mX);
    return mX;
  }
}
