/**
 * A203430 Vandermonde determinant of the first n numbers (1,3,4,6,7,9,10,...) = (j+floor(j/2)).
 * @author Georg Fischer
 */
public class A203430 extends Sequence1 {

  protected int mN = 0;

  @Override
  public Z next() {
    ++mN;
    Z prod = Z.ONE;
    for (int k = 2; k <= mN; ++k) {
      for (int j = 1; j <= k - 1; ++j) {
        prod = prod.multiply(Z.valueOf(k + k / 2).subtract(Z.valueOf(j + j / 2)));
      }
    }
    return prod;
  }
}
