package irvine.oeis.a243;

import irvine.math.z.Z;
import irvine.oeis.Sequence;

/**
 * A243063 Numbers generated by a Fibonacci-like sequence in which zeros are suppressed.
 * @author Georg Fischer
 */
public class A243063 implements Sequence {

  private int mN = 0;
  private Z mA = Z.ZERO;
  private Z mB = Z.ONE;

  @Override
  public Z next() {
    if (++mN <= 1) {
      return Z.ONE;
    }
    final Z result = new Z(mA.add(mB).toString().replace("0", ""));
    mA = mB;
    mB = result;
    return result;
  }
}
