package irvine.math.function;

import irvine.math.z.Z;
import junit.framework.TestCase;

/**
 * Tests the corresponding class.
 * @author Sean A. Irvine
 */
public class FactorialTest extends TestCase {

  public void test() {
    final Function1 f = new Factorial();
    assertEquals(Z.ONE, f.z(0));
    assertEquals(Z.ONE, f.z(1));
    assertEquals("2", f.z(2).toString());
    assertEquals("6", f.z(3).toString());
    assertEquals("24", f.z(4).toString());
    assertEquals("120 ", "120", f.z(5).toString());
    assertEquals("720", "720", f.z(6).toString());
    assertEquals(3628800, f.l(10));
    assertEquals("2432902008176640000", f.z(20).toString());
    assertEquals("265252859812191058636308480000000", f.z(30).toString());
    assertEquals("30414093201713378043612608166064768844377641568960512000000000000", f.z(50).toString());
    assertEquals("93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000", f.z(100).toString());
    assertEquals("", f.z(200).toString());
  }
}
